UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Приветствую $N', `Text0_1_loc8` = 'Приветствую $N' WHERE `entry` = 68;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Еще столько всего надо сделать! Союз установил нам жесткие сроки – и лучше бы их не срывать!' WHERE `entry` = 518;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Директор Ридлвокс назначил меня главным в Союзе по разработке резака. Всего несколько месяцев на новом месте – и уже неприятности! Не поможешь мне, $n?' WHERE `entry` = 519;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Видишь, новый резак Торговой компании лучше моего последнего образца во всем – от заменяемых рук до управлямых клешней! Мой босс поставил мне очень жесткие сроки, но ни я, ни мои парни не можем ничего придумать. $b$bПоэтому мы решили "одолжить" один из их прототипов. Я послал Визлкранка в Нефтяное болото, но он до сих пор не вернулся!' WHERE `entry` = 520;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Подойди поближе, $gюноша:девушка;. Некогда я слышал стук сердца добычи, а теперь тебе придется говорить погромче, если хочешь, чтоб я тебя расслышал.' WHERE `entry` = 522;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Много-много лет я проходил по этим равнинам и охотился тут на крупного зверя. Никто никогда не мог противостоять мне... пока я не повстречался с Призрачным Воем.$B$BДавным-давно огромный волк был союзником тауренов и храбро сражался с Пылающим Легионом. Но его жестоко ранил демон, и рана так никогда и не зажила. В конце концов... Призрачный Вой обезумел.$B$BТеперь он носится по равнинам, терзаемый болью, ища способ положить ей конец. К стыду своему должен сказать: когда мы сразились, я не сумел убить его...' WHERE `entry` = 523;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Здравствуй, маг. Тебе нужны новые знания о чудесном мире магии?', `Text0_1_loc8` = 'Здравствуй, маг. Тебе нужны новые знания о чудесном мире магии?' WHERE `entry` = 538;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Здравствуй, |3-6($c). Вот что я тебе посоветую: будь $gосторожен:осторожна; с магией. Она может легко погубить новичка.', `Text0_1_loc8` = 'Здравствуй, |3-6($c). Вот что я тебе посоветую: будь $gосторожен:осторожна; с магией. Она может легко погубить новичка.' WHERE `entry` = 539;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Приветствую. Я наставник магов, а ты маг.', `Text0_1_loc8` = 'Приветствую. Я наставник магов, а ты маг.' WHERE `entry` = 558;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Добро пожаловать, |3-6($c). Позволь дать тебе один совет: путешествуя по этому миру, будь $gосторожен:осторожна; с магией, иначе поплатишься жизнью.', `Text0_1_loc8` = 'Добро пожаловать, |3-6($c). Позволь дать тебе один совет: путешествуя по этому миру, будь $gосторожен:осторожна; с магией, иначе поплатишься жизнью.' WHERE `entry` = 559;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Приветствую. Я гном, наставник магов. Ну а вы – маг.', `Text0_1_loc8` = 'Приветствую. Я гном, наставница магов. Ну а вы – маг.' WHERE `entry` = 560;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Здравствуйте, |3-6($c). Хотите совет? Подружитесь с магами! У нас много чего интересного в рукаве припрятано.', `Text0_1_loc8` = 'Здравствуйте, |3-6($c). Хотите совет? Подружитесь с магами! У нас много чего интересного в рукаве припрятано.' WHERE `entry` = 561;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Приветствую. Я обучаю магов из нежити, а вы, стало быть, занимаетесь магией?', `Text0_1_loc8` = 'Приветствую. Я обучаю магов из нежити, а вы, стало быть, занимаетесь магией?' WHERE `entry` = 562;
UPDATE `locales_npc_text` SET `Text0_0_loc8` = 'Здравствуй, |3-6($c). Вот что я тебе посоветую: будь $gосторожен:осторожна; с магией. Она может легко погубить новичка.', `Text0_1_loc8` = 'Здравствуй, |3-6($c). Вот что я тебе посоветую: будь $gосторожен:осторожна; с магией. Она может легко погубить новичка.' WHERE `entry` = 563;
